mapper.update:
update Seat
set hall_id = #{hallId,jdbcType=INTEGER},
  seat_row = #{seatRow,jdbcType=INTEGER},
  seat_col = #{seatCol,jdbcType=INTEGER},
  seat_status = #{seatStatus,jdbcType=SMALLINT}
where seat_id = #{seatId,jdbcType=INTEGER}

mapper.select:
select seat_id, hall_id, seat_row, seat_col, seat_status
from Seat
where hall_id = #{hallId,jdbcType=INTEGER}

select seat_id, hall_id, seat_row, seat_col, seat_status
from Seat
where seat_row = #{seatRow} and seat_col = #{seatCol}
